export default (function(a,b,c,d,e,f){return {data:{"page-masters-of-scale":{categoryId:a,categoryName:"Business",created:"2023-01-06 11:10:00",description:"\nDescription\nHow do companies grow from zero to a gazillion? Legendary Silicon Valley investor \u002F entrepreneur Reid Hoffman tests his theories with famous founders and leaders.",id:18,links:[{languageId:a,languageName:b,platformIcon:"home",platformId:a,platformName:"Website",typeId:d,typeName:"Social",url:"https:\u002F\u002Fmastersofscale.com\u002F"},{languageId:a,languageName:b,platformIcon:"youtube",platformId:c,platformName:"YouTube",typeId:e,typeName:"Video",url:"https:\u002F\u002Fwww.youtube.com\u002F@mastersofscale1013"},{languageId:a,languageName:b,platformIcon:"googlepodcasts",platformId:e,platformName:"Google Podcasts",typeId:c,typeName:f,url:"https:\u002F\u002Fpodcasts.google.com\u002Ffeed\u002FaHR0cHM6Ly9yc3MuYXJ0MTkuY29tL21hc3RlcnMtb2Ytc2NhbGU"},{languageId:a,languageName:b,platformIcon:"spotify",platformId:d,platformName:"Spotify",typeId:c,typeName:f,url:"https:\u002F\u002Fopen.spotify.com\u002Fshow\u002F1bJRgaFZHuzifad4IAApFR"}],showDate:"21.06.2022 12:00",slug:"masters-of-scale",title:"Masters of Scale",updated:"2022-06-21 12:00:08",content:"\u003Cp\u003EAward-winning business advice from Silicon Valley and beyond. Iconic CEOs, from Nike to Netflix, Starbucks to Slack, share the strategies that helped them grow from startups into global brands — and to weather crisis when it strikes.\u003C\u002Fp\u003E\n\n\u003Cp\u003EOur two formats help tell the complete story of how a business grows, survives and thrives, and the mindsets of growth that keep leaders in the game. On each episode of our classic format, host Reid Hoffman — LinkedIn cofounder, Greylock partner and legendary Silicon Valley investor — proves an unconventional theory about how businesses scale, asking his guests to share their stories of entrepreneurship, leadership, strategy, management, fundraising.\u003C\u002Fp\u003E\n\n\u003Cp\u003EYou’ll hear the human journey too — failures, setbacks, learnings.  From our Rapid Response format, you can expect real-time wisdom from business leaders in fast-changing situations. Hosted by Bob Safian, past editor in chief of Fast Company, these episodes tackle crisis response, rebuilding, diversity & inclusion, leadership change and much more.\u003C\u002Fp\u003E",videoId:"F24FZz1DczA",videoName:"Delivering human dignity at scale, with Amber Galloway",prev:{id:17,title:"Youpreneur",slug:"youpreneur"}}},prerenderedAt:1676157988101}}(1,"English",2,5,3,"Podcast"))